mapper.select:
select id, name, dong, ho 
from resident
where name= #{name}

select *
from candidate
where seq=#{seq}
and id=#{id}

mapper.update:
update candidate		
set job=#{job},
	gender=#{gender},
	content=#{content}
where id=#{id}
and seq=#{seq}

mapper.delete:
update candidate
set state='del'
where id = #{id}
