mapper.select:

		select b.*  from(  select a.*, rownum rn from(  
                select *
				from board
				where type=#{type}

					and title like '%' || #{title} || '%'


					and content like '%' || #{content} || '%'


					and writer = #{writer}

				order by 1 desc
        ) a ) b where rn between #{start} and #{end}
