mapper.select:
	select a.* from( select b.*, rownum rn from (
	SELECT * 
	FROM qna

	order by qnano desc
) b ) a where rn between #{start} and #{end}

mapper.insert:
insert into qna( qnano, title, content, defdate, writer, type
, deffile
) values( def_seq.nextval, #{title}, #{content}, sysdate, #{writer}, #{type}
, #{deffile}
)
